INSERT INTO `laws` (`id`, `text`, `sanctions`, `source`, `section`) VALUES
(1, '16.1. Намын эд хөрөнгөд үл хөдлөх, хөдлөх эд хөрөнгө байна.', NULL, 'УЛС ТӨРИЙН НАМЫН ТУХАЙ', NULL),
(2, '16.2. Намын хөрөнгөд дараахь зүйл хамаарна: Гишүүний татвар, гишүүн, талархан дэмжигч иргэн, хуулийн этгээдээс өгсөн хандив, төрөөс үзүүлэх санхүүгийн дэмжлэг, намын бэлэгдэл бүхий бэлэг дурсгалын зүйлс, намын өмчлөлд байгаа эд юмс, намын хэвлэл, мэдээлэл, сурталчилгааны орлого, өөрийн өмчлөлийн эд хөрөнгийг худалдах буюу түрээслүүлж ашигласнаас орсон орлого, намын мөнгөн хөрөнгийг банкинд хадгалуулсны хүүв', NULL, 'УЛС ТӨРИЙН НАМЫН ТУХАЙ', NULL),
(3, '16.3. Энэ хуулийн 16 дугаар зүйлд зааснаас бусад хэлбэрээр ашиг олох аж ахуйн үйл ажиллагаа эрхлэхийг хориглоно.', '24.2.3. энэ хуулийн 16.3-т заасныг зөрчин хуулиар хориглосон бусад хэлбэрээр ашиг олсон бол олсон ашгийг хурааж, албан тушаалтанд 40000-60000 хүртэл төгрөгийн, намд 200000-250000 хүртэл төгрөгийн торгууль ногдуулах;', 'УЛС ТӨРИЙН НАМЫН ТУХАЙ', NULL),
(4, '17 дугаар зүйл. Намын орлогын зарцуулалт : 17.1. Намын орлогыг зөвхөн намын мөрийн хөтөлбөр, дүрэмд тусгагдсан үйл ажиллагаа явуулахад зарцуулна. 17.2. Намын орлогыг өөр зүйлд зарцуулах, гишүүдэд ногдол ашиг болгон хуваарилахыг хориглоно.', '24.2.4. энэ хуулийн 17.2-т заасныг зөрчин намын орлогоос ногдол ашиг хуваарилах, иргэн, аж ахуйн нэгж, байгууллагын төлбөрийг төлөх, өөр зүйлд зарцуулсан бол албан тушаалтанд 40000-60000 хүртэл төгрөгийн, намд 200000-250000 хүртэл төгрөгийн торгууль ногдуулах;', 'УЛС ТӨРИЙН НАМЫН ТУХАЙ', NULL),
(5, '18.2. Гишүүн, дэмжигчид үзэл бодол, итгэл үнэмшлээрээ намыг дэмжин хандив өргөж болно. Хандивыг зөвхөн тухайн шатны намын байгууллагын дансаар дамжуулж авна.', '24.2.5. энэ хуулийн 18.2, 18.3-т заасныг зөрчсөн бол данснаас гадуур авсан болон илүү авсан хандивыг хурааж, албан тушаалтанд 40000-60000 хүртэл төгрөгийн, намд 200000-250000 хүртэл төгрөгийн торгууль ногдуулах;', 'УЛС ТӨРИЙН НАМЫН ТУХАЙ', NULL),
(6, '18.3. Намд зориулж нэг удаа өгөх хандивын дээд хэмжээ хуулийн этгээдээс арван сая, иргэдээс нэг сая төгрөгөөс хэтэрч болохгүй бөгөөд нэг хандивлагч намын нэг байгууллагад жилд 2-оос дээш удаа хандив өгөхийг хориглоно. Намд хандив өгсний төлөө албан тушаал, хувийн эрх ашиг хөөцөлдөх, шахалт үзүүлэхийг хориглоно.', '24.2.5. энэ хуулийн 18.2, 18.3-т заасныг зөрчсөн бол данснаас гадуур авсан болон илүү авсан хандивыг хурааж, албан тушаалтанд 40000-60000 хүртэл төгрөгийн, намд 200000-250000 хүртэл төгрөгийн торгууль ногдуулах;', 'УЛС ТӨРИЙН НАМЫН ТУХАЙ', NULL),
(7, '18.4. Нам хандивынхаа талаар нийтэд мэдээлж байна.', NULL, 'УЛС ТӨРИЙН НАМЫН ТУХАЙ', NULL),
(8, '18.5. Гишүүний татвар болон хандивыг тооцооны нэг дансанд байршуулна.', NULL, 'УЛС ТӨРИЙН НАМЫН ТУХАЙ', NULL),
(9, '18.6. Сонгуулийн үйл ажиллагаанд зориулж намд өргөсөн хандивын асуудлыг сонгуулийн хууль тогтоомжоор зохицуулна', NULL, 'УЛС ТӨРИЙН НАМЫН ТУХАЙ', NULL),
(10, '18.7. Дараахь этгээдээс хандив авахыг хориглоно:18.7.1. арван найман насанд хүрээгүй Монгол Улсын иргэн; 18.7.2. төрийн болон төрийн өмчийн оролцоотой аж ахуйн нэгж, байгууллага; 18.7.3. шашны байгууллага; 18.7.4. олон улсын байгууллага, гадаадын иргэн, хуулийн этгээд, харьяалалгүй хүн; 18.7.5. байгуулагдаад нэг жил болоогүй хуулийн этгээд; 18.7.6. тодорхой нэр хаяггүй этгээд; 18.7.7. дампуурсан болон банкны хугацаа хэтэрсэн зээлийн өртэй хуулийн этгээд; 18.7.8. хуулиар хориглосон бусад этгээд.', '24.2.6. энэ хуулийн 18.7-д заасан этгээдээс хандив авсан бол хандивыг хурааж, тухайн намд 200000-250000 хүртэл төгрөгийн, албан тушаалтанд 50000-60000 хүртэл төгрөгийн торгууль ногдуулах.', 'УЛС ТӨРИЙН НАМЫН ТУХАЙ', NULL),
(11, '18.8. Олон улсын байгууллага болон улс төрийн үйл ажиллагаа явуулдаг гадаадын байгууллага, сантай хамтран зохион байгуулах арга хэмжээ, хэрэгжүүлэх төслийн санхүүжилт энэ хуулийн 18.7.4-т хамаарахгүй', NULL, 'УЛС ТӨРИЙН НАМЫН ТУХАЙ', NULL),
(12, '19.1. Улсын Их Хурлын сонгуулиар тухайн намын авсан саналыг нийт хүчинтэй саналын тоотой харьцуулах зарчмаар нэг саналыг мянган төгрөгөөр тооцож, сонгуулийн дүн гарсны дараа гурван сарын дотор багтаан Улсын Их Хуралд суудал бүхий намд төр нэг удаа мөнгөн дэмжлэг үзүүлнэ.', NULL, 'УЛС ТӨРИЙН НАМЫН ТУХАЙ', NULL),
(13, '19.2. Улсын Их Хуралд суудал бүхий намд түүний авсан суудлын тоогоор Улсын Их Хурлын гишүүний нэг суудлыг жилд арван сая төгрөгөөр тооцож, Улсын Их Хурлын тухайн бүрэн эрхийн хугацаанд улирал тутам улсын төсвөөс санхүүжилт олгоно. Энэхүү санхүүжилтийн 50 хувийг Улсын Их Хурлын гишүүдийн сонгуулийн тойрогт явуулах үйл ажиллагаанд зарцуулна', NULL, 'УЛС ТӨРИЙН НАМЫН ТУХАЙ', NULL),
(14, '20.1. Намын санхүүгийн үйл ажиллагаанд тухайн намын хяналтын байгууллага хяналт тавина.', NULL, 'УЛС ТӨРИЙН НАМЫН ТУХАЙ', NULL),
(15, '20.2. Намын төв байгууллага нь харьяа байгууллагын санхүүгийн тайлан гаргасны дараа нэгдсэн тайлан гаргана.', NULL, 'УЛС ТӨРИЙН НАМЫН ТУХАЙ', NULL),
(16, '20.3. Нам санхүүгийн үйл ажиллагаандаа жил бүр аудит хийлгэн баталгаажуулж, нийтэд мэдээлнэ.', '24.2.7. энэ хуулийн 20.3-т заасныг зөрчин намын санхүүгийн үйл ажиллагаанд аудит хийлгэхээс зайлсхийсэн, аудит хийлгэн баталгаажуулсан боловч нийтэд мэдээлээгүй бол албан тушаалтанд 40000-60000 хүртэл төгрөгийн, намд 100000-250000 хүртэл төгрөгийн торгууль ногдуулах;', 'УЛС ТӨРИЙН НАМЫН ТУХАЙ', NULL),
(17, '20.4. Намын удирдлага, намын санхүүгийн албаны дарга, ерөнхий менежер, түүнтэй адилтгах албан тушаалтан нь аль нэгэн улс төрийн сан, нийгэмлэг, аж ахуйн нэгж, байгууллагад санхүүтэй холбогдсон ажил, албан тушаал эрхлэхийг хориглоно.', '24.2.8. энэ хуулийн 20.4-т заасныг зөрчин ажил, албан тушаал эрхэлсэн бол албан тушаалтанд 50000-60000 хүртэл төгрөгийн торгууль ногдуулах', 'УЛС ТӨРИЙН НАМЫН ТУХАЙ', NULL),
(18, '20.5. Намын дарга намын санхүү, төсвийн үйл ажиллагаанд захиран зарцуулах эрхтэйгээр оролцохыг хориглоно.', '24.2.9. энэ хуулийн 20.5-д заасныг зөрчсөн бол албан тушаалтанд 50000-60000 хүртэл төгрөгийн торгууль ногдуулах', 'УЛС ТӨРИЙН НАМЫН ТУХАЙ', NULL),
(19, '20.6. Монгол Улсын Ерөнхийлөгч, Улсын Их Хурлын болон Аймаг, нийслэл, сум, дүүргийн иргэдийн Төлөөлөгчдийн Хурлын сонгуулийн болон нөхөн сонгуулийн зардлын тайланг гаргахтай холбогдох харилцааг сонгуулийн хууль тогтоомжоор зохицуулна.', NULL, 'УЛС ТӨРИЙН НАМЫН ТУХАЙ', NULL),
(20, '24.2.3. энэ хуулийн 16.3-т заасныг зөрчин хуулиар хориглосон бусад хэлбэрээр ашиг олсон бол олсон ашгийг хурааж, албан тушаалтанд 40000-60000 хүртэл төгрөгийн, намд 200000-250000 хүртэл төгрөгийн торгууль ногдуулах;', NULL, 'УЛС ТӨРИЙН НАМЫН ТУХАЙ', NULL),
(21, '24.2.4. энэ хуулийн 17.2-т заасныг зөрчин намын орлогоос ногдол ашиг хуваарилах, иргэн, аж ахуйн нэгж, байгууллагын төлбөрийг төлөх, өөр зүйлд зарцуулсан бол албан тушаалтанд 40000-60000 хүртэл төгрөгийн, намд 200000-250000 хүртэл төгрөгийн торгууль ногдуулах;', NULL, 'УЛС ТӨРИЙН НАМЫН ТУХАЙ', NULL),
(22, '24.2.5. энэ хуулийн 18.2, 18.3-т заасныг зөрчсөн бол данснаас гадуур авсан болон илүү авсан хандивыг хурааж, албан тушаалтанд 40000-60000 хүртэл төгрөгийн, намд 200000-250000 хүртэл төгрөгийн торгууль ногдуулах;', NULL, 'УЛС ТӨРИЙН НАМЫН ТУХАЙ', NULL),
(23, '24.2.6. энэ хуулийн 18.7-д заасан этгээдээс хандив авсан бол хандивыг хурааж, тухайн намд 200000-250000 хүртэл төгрөгийн, албан тушаалтанд 50000-60000 хүртэл төгрөгийн торгууль ногдуулах.', NULL, 'УЛС ТӨРИЙН НАМЫН ТУХАЙ', NULL),
(24, '24.2.7. энэ хуулийн 20.3-т заасныг зөрчин намын санхүүгийн үйл ажиллагаанд аудит хийлгэхээс зайлсхийсэн, аудит хийлгэн баталгаажуулсан боловч нийтэд мэдээлээгүй бол албан тушаалтанд 40000-60000 хүртэл төгрөгийн, намд 100000-250000 хүртэл төгрөгийн торгууль ногдуулах;', NULL, 'УЛС ТӨРИЙН НАМЫН ТУХАЙ', NULL),
(25, '24.2.8. энэ хуулийн 20.4-т заасныг зөрчин ажил, албан тушаал эрхэлсэн бол албан тушаалтанд 50000-60000 хүртэл төгрөгийн торгууль ногдуулах', NULL, 'УЛС ТӨРИЙН НАМЫН ТУХАЙ', NULL),
(26, '24.2.9. энэ хуулийн 20.5-д заасныг зөрчсөн бол албан тушаалтанд 50000-60000 хүртэл төгрөгийн торгууль ногдуулах', NULL, 'УЛС ТӨРИЙН НАМЫН ТУХАЙ', NULL),
(27, '23.4.Нам, эвсэл нь сонгуульд оролцох хүсэлтдээ дараах баримт бичгийг хавсарган Сонгуулийн ерөнхий хороонд ирүүлнэ: 23.4.5.сүүлийн нэг жилийн хугацаанд тухайн нам буюу эвсэлд нэгдэн орсон намуудад иргэн, хуулийн этгээдээс өгсөн хандивын аудитын байгууллагаар хянуулж баталгаажуулсан тайлан', '25.3.Дараах тохиолдолд нам, эвслийг бүртгэхээс татгалзана: 25.3.2.энэ хуулийн 23.4, 23.5-д заасан баримт бичиг дутуу, эсхүл хуурамч бол; 25.3.3.энэ хуулийн 23.4, 23.5-д заасан баримт бичгийг энэ хуулийн 23.3-т заасан хугацаа дууссанаас хойш ирүүлсэн бол;', 'МОНГОЛ УЛСЫН ИХ ХУРЛЫН СОНГУУЛИЙН ТУХАЙ', NULL),
(28, '25.3.Дараах тохиолдолд нам, эвслийг бүртгэхээс татгалзана: 25.3.2.энэ хуулийн 23.4, 23.5-д заасан баримт бичиг дутуу, эсхүл хуурамч бол; 25.3.3.энэ хуулийн 23.4, 23.5-д заасан баримт бичгийг энэ хуулийн 23.3-т заасан хугацаа дууссанаас хойш ирүүлсэн бол;', NULL, 'МОНГОЛ УЛСЫН ИХ ХУРЛЫН СОНГУУЛИЙН ТУХАЙ', NULL),
(29, '34.4.Нам, эвслийн сонгуулийн мөрийн хөтөлбөрт санхүүгийн тодорхой эх үүсвэр шаардсан арга хэмжээ тусгагдсан тохиолдолд түүнийг хэрэгжүүлэхэд шаардагдах зардлын дүн нь Төсвийн тогтвортой байдлын тухай хуульд заасан төсвийн тусгай шаардлагад нийцсэн байна.', NULL, 'МОНГОЛ УЛСЫН ИХ ХУРЛЫН СОНГУУЛИЙН ТУХАЙ', NULL),
(30, '34.5.Нам, эвсэл сонгуулийн мөрийн хөтөлбөрөө Төсвийн тогтвортой байдлын тухай хуульд заасан төсвийн тусгай шаардлагад нийцэж байгаа эсэх талаар төрийн аудитын байгууллагаар дүгнэлт гаргуулна.', NULL, 'МОНГОЛ УЛСЫН ИХ ХУРЛЫН СОНГУУЛИЙН ТУХАЙ', NULL),
(31, '34.7.Нам, эвсэл болон бие даан нэр дэвшигч нь сонгуулийн мөрийн хөтөлбөрийг хэрэгжүүлэх санхүүгийн эх үүсвэрийн талаарх тооцоо, судалгааг хөндлөнгийн хяналтын байгууллагаар хянуулсан байна.', NULL, 'МОНГОЛ УЛСЫН ИХ ХУРЛЫН СОНГУУЛИЙН ТУХАЙ', NULL),
(32, '37.1.Сонгуулийн ерөнхий хороо тойрогт нэг нэр дэвшигчээс сонгуульд зарцуулах зардлын дээд хэмжээг тухайн тойргийн нутаг дэвсгэрийн хэмжээ, байршил, хүн амын тоо зэргийг харгалзан тойрог тус бүрээр, түүнчлэн нам, эвслээс сонгуульд зарцуулах зардлын дээд хэмжээг сонгуулийн жилийн хоёрдугаар сарын нэгний дотор тус тус тогтооно.\r\n', '37.3.Энэ хуулийн 37.1-д заасан сонгуулийн зардлын дээд хэмжээг хэтрүүлсэн нам, эвслийг нэг сарын хөдөлмөрийн хөлсний доод хэмжээг арван таваас хорь дахин нэмэгдүүлсэнтэй тэнцэх хэмжээний төгрөгөөр, нэр дэвшигчийг нэг сарын хөдөлмөрийн хөлсний доод хэмжээг арваас арван тав дахин нэмэгдүүлсэнтэй тэнцэх хэмжээний төгрөгөөр тус тус торгоно.\r\n', 'МОНГОЛ УЛСЫН ИХ ХУРЛЫН СОНГУУЛИЙН ТУХАЙ', NULL),
(33, '37.2.Энэ хуулийн 37.1-д заасан сонгуульд зарцуулах зардлын дээд хэмжээнд багтаан нам, эвслийн сонгуулийн зардлыг тухайн нам, эвсэл нь, нам, эвслээс нэр дэвшигчийн сонгуулийн зардлыг тухайн нам, эвсэл болон нэр дэвшигч тус тус хариуцах бөгөөд бие даан нэр дэвшигч сонгуулийн зардлаа өөрөө хариуцна.\r\n', NULL, 'МОНГОЛ УЛСЫН ИХ ХУРЛЫН СОНГУУЛИЙН ТУХАЙ', NULL),
(34, '37.3.Энэ хуулийн 37.1-д заасан сонгуулийн зардлын дээд хэмжээг хэтрүүлсэн нам, эвслийг нэг сарын хөдөлмөрийн хөлсний доод хэмжээг арван таваас хорь дахин нэмэгдүүлсэнтэй тэнцэх хэмжээний төгрөгөөр, нэр дэвшигчийг нэг сарын хөдөлмөрийн хөлсний доод хэмжээг арваас арван тав дахин нэмэгдүүлсэнтэй тэнцэх хэмжээний төгрөгөөр тус тус торгоно.\r\n', NULL, 'МОНГОЛ УЛСЫН ИХ ХУРЛЫН СОНГУУЛИЙН ТУХАЙ', NULL),
(35, '38.1.Нам, эвсэл сонгуульд оролцохоо илэрхийлж Сонгуулийн ерөнхий хороонд бүртгүүлснээс хойш гурав хоногийн дотор тойрогт нэр дэвшигчдээс бусад нам, эвслээс нэр дэвшигчдийн сонгуулийн зардлын нэг дансыг банкинд нээлгэн, Сонгуулийн ерөнхий хороо болон Үндэсний аудитын газарт бичгээр мэдэгдэж, нийтэд зарлана.\r\n', NULL, 'МОНГОЛ УЛСЫН ИХ ХУРЛЫН СОНГУУЛИЙН ТУХАЙ', NULL),
(36, '38.2.Тойрогт нэр дэвшүүлсэн нам, эвсэл нь нэр дэвшигч тус бүр дээр, түүнчлэн бие даан нэр дэвшигч нь өөрийн сонгуулийн зардлын дансыг тойргийн хороонд бүртгүүлснээс хойш гурав хоногийн дотор банкинд нээлгэн, Сонгуулийн ерөнхий хороо болон Үндэсний аудитын газарт бичгээр мэдэгдэж, нийтэд зарлана.\r\n', NULL, 'МОНГОЛ УЛСЫН ИХ ХУРЛЫН СОНГУУЛИЙН ТУХАЙ', NULL),
(37, '38.2.Тойрогт нэр дэвшүүлсэн нам, эвсэл нь нэр дэвшигч тус бүр дээр, түүнчлэн бие даан нэр дэвшигч нь өөрийн сонгуулийн зардлын дансыг тойргийн хороонд бүртгүүлснээс хойш гурав хоногийн дотор банкинд нээлгэн, Сонгуулийн ерөнхий хороо болон Үндэсний аудитын газарт бичгээр мэдэгдэж, нийтэд зарлана.\r\n', NULL, 'МОНГОЛ УЛСЫН ИХ ХУРЛЫН СОНГУУЛИЙН ТУХАЙ', NULL),
(38, '38.3.Нам, эвслийн тойрогт нэр дэвшигчдээс нам, эвслээс бусад нэр дэвшигч нь энэ хуулийн 38.1-д зааснаас өөр данс, тойрогт нэр дэвшигч нь энэ хуулийн 38.2-т зааснаас өөр данс нээлгэхийг тус тус хориглоно.\r\n', '38.6.Энэ хуулийн 38.3-38.5-д заасныг зөрчсөн нэр дэвшигчийг нэг сарын хөдөлмөрийн хөлсний доод хэмжээг гурваас тав дахин нэмэгдүүлсэнтэй тэнцэх хэмжээний төгрөгөөр, ... тус тус торгох ба энэ хуулийн 38.1, 38.2-т заасан данснаас гадуур өгсөн, энэ хуулийн 38.3-т заасныг зөрчиж нээлгэсэн дансанд төвлөрүүлсэн хөрөнгийг хурааж, улсын орлого болгоно.\r\n', 'МОНГОЛ УЛСЫН ИХ ХУРЛЫН СОНГУУЛИЙН ТУХАЙ', NULL),
(39, '38.4.Энэ хуулийн 38.1, 38.2-т заасан дансанд нам, эвсэл, нэр дэвшигчийн өөрийн хөрөнгө, дэмжигч байгууллага, иргэдээс оруулсан хандивыг төвлөрүүлж, мөн дансаар дамжуулан зориулалтаар нь зарцуулна.\r\n', '38.6.Энэ хуулийн 38.3-38.5-д заасныг зөрчсөн нэр дэвшигчийг нэг сарын хөдөлмөрийн хөлсний доод хэмжээг гурваас тав дахин нэмэгдүүлсэнтэй тэнцэх хэмжээний төгрөгөөр, энэ хуулийн 38.4, 38.5-д заасныг зөрчсөн нам, эвслийг нэг сарын хөдөлмөрийн хөлсний доод хэмжээг таваас долоо дахин нэмэгдүүлсэнтэй тэнцэх хэмжээний төгрөгөөр тус тус торгох ба энэ хуулийн 38.1, 38.2-т заасан данснаас гадуур өгсөн, энэ хуулийн 38.3-т заасныг зөрчиж нээлгэсэн дансанд төвлөрүүлсэн хөрөнгийг хурааж, улсын орлого болгоно.\r\n', 'МОНГОЛ УЛСЫН ИХ ХУРЛЫН СОНГУУЛИЙН ТУХАЙ', NULL),
(40, '38.5.Энэ хуулийн 38.1, 38.2-т заасан дансанд төвлөрсөн хөрөнгийг нам, эвслийн сонгуулийн мөрийн хөтөлбөрийг тайлбарлан таниулах, нэр дэвшигчийг сурталчлах, сонгуулийн уулзалт, хурал, цуглаан зохион байгуулах, нэр дэвшигч, нам, эвслийн сонгууль эрхэлсэн байгууллага, түүний ажилтан, нэр дэвшигчийн шадар туслагчийн бичиг хэрэг, шуудан, холбоо, унааны болон албан томилолт, хөлс, урамшуулал зэрэг зардлыг санхүүжүүлэхэд зарцуулна.\r\n', '38.6.Энэ хуулийн 38.3-38.5-д заасныг зөрчсөн нэр дэвшигчийг нэг сарын хөдөлмөрийн хөлсний доод хэмжээг гурваас тав дахин нэмэгдүүлсэнтэй тэнцэх хэмжээний төгрөгөөр, энэ хуулийн 38.4, 38.5-д заасныг зөрчсөн нам, эвслийг нэг сарын хөдөлмөрийн хөлсний доод хэмжээг таваас долоо дахин нэмэгдүүлсэнтэй тэнцэх хэмжээний төгрөгөөр тус тус торгох ба энэ хуулийн 38.1, 38.2-т заасан данснаас гадуур өгсөн, энэ хуулийн 38.3-т заасныг зөрчиж нээлгэсэн дансанд төвлөрүүлсэн хөрөнгийг хурааж, улсын орлого болгоно.\r\n', 'МОНГОЛ УЛСЫН ИХ ХУРЛЫН СОНГУУЛИЙН ТУХАЙ', NULL),
(41, '38.6.Энэ хуулийн 38.3-38.5-д заасныг зөрчсөн нэр дэвшигчийг нэг сарын хөдөлмөрийн хөлсний доод хэмжээг гурваас тав дахин нэмэгдүүлсэнтэй тэнцэх хэмжээний төгрөгөөр, энэ хуулийн 38.4, 38.5-д заасныг зөрчсөн нам, эвслийг нэг сарын хөдөлмөрийн хөлсний доод хэмжээг таваас долоо дахин нэмэгдүүлсэнтэй тэнцэх хэмжээний төгрөгөөр тус тус торгох ба энэ хуулийн 38.1, 38.2-т заасан данснаас гадуур өгсөн, энэ хуулийн 38.3-т заасныг зөрчиж нээлгэсэн дансанд төвлөрүүлсэн хөрөнгийг хурааж, улсын орлого болгоно.\r\n', NULL, 'МОНГОЛ УЛСЫН ИХ ХУРЛЫН СОНГУУЛИЙН ТУХАЙ', NULL),
(42, '38.7.Энэ хуулийн 38.3, 38.4-т заасныг зөрчсөн нь нэр дэвшигчийг нэрийн жагсаалтаас хасах үндэслэл болно\r\n', NULL, 'МОНГОЛ УЛСЫН ИХ ХУРЛЫН СОНГУУЛИЙН ТУХАЙ', NULL),
(43, '39.1.Нам, эвсэл, нэр дэвшигчид өгөх хандивын дээд хэмжээ хувь хүнээс нэг сая төгрөг хүртэл, хуулийн этгээдээс гурван сая төгрөг хүртэл байж болно.\r\n', '39.11.Энэ хуулийн 39.1-д заасан хэмжээнээс илүү гарсан, түүнчлэн энэ хуулийн 39.7-д заасан этгээдээс авсан хандивыг хурааж, улсын орлого болгоно.\r\n', 'МОНГОЛ УЛСЫН ИХ ХУРЛЫН СОНГУУЛИЙН ТУХАЙ', NULL),
(44, '39.2.Энэ хуулийн 39.1-д заасан хандивын хэмжээг энэ хуулийн 38.1, 38.2-т заасан дансанд төвлөрүүлсэн хөрөнгийн нийлбэрээр тооцно.\r\n', NULL, 'МОНГОЛ УЛСЫН ИХ ХУРЛЫН СОНГУУЛИЙН ТУХАЙ', NULL),
(45, '39.3.Хуулийн этгээдээс хандив өгөх тухай шийдвэрийг түүний эрх бүхий байгууллага гаргана.\r\n', NULL, 'МОНГОЛ УЛСЫН ИХ ХУРЛЫН СОНГУУЛИЙН ТУХАЙ', NULL),
(46, ' 39.4.Мөнгөн хандивыг зөвхөн энэ хуулийн 38.1, 38.2-т заасан дансаар дамжуулж өгнө.\r\n', NULL, 'МОНГОЛ УЛСЫН ИХ ХУРЛЫН СОНГУУЛИЙН ТУХАЙ', NULL),
(47, '39.5.Хандивыг мөнгөн бус хэлбэрээр өгвөл түүнийг мөнгөн хэлбэрт шилжүүлэн үнэлж, энэ хуулийн 39.1-д заасан хандивын дүнд оруулан тооцно.\r\n', NULL, 'МОНГОЛ УЛСЫН ИХ ХУРЛЫН СОНГУУЛИЙН ТУХАЙ', NULL),
(48, '39.6.Нам, эвсэл, бие даан нэр дэвшигч нь сонгуулийн хандивын хэмжээ, хэлбэрийг сонгуулийн зардлын тайланд тусгана.\r\n', NULL, 'МОНГОЛ УЛСЫН ИХ ХУРЛЫН СОНГУУЛИЙН ТУХАЙ', NULL),
(49, '39.7.Дараах этгээдээс хандив авахыг хориглоно: 39.7.1.гадаад улс буюу гадаадын байгууллага /хамтарсан байгууллагын гадаадын оролцогч/;39.7.2.олон улсын байгууллага; 39.7.3.төрийн болон нутгийн өөрөө удирдах байгууллага; 39.7.4.гадаад улсын иргэн; 39.7.5.харьяалалгүй хүн; 39.7.6.сонгууль товлон зарласан өдөр арван найман нас хүрээгүй Монгол Улсын иргэн; 39.7.7.төрийн болон орон нутгийн өмчит, төрийн болон орон нутгийн өмчийн оролцоотой хуулийн этгээд; 39.7.8.шүүхийн шийдвэрээр тогтоогдсон албан татварын өр төлбөртэй, дампуурсан, банкны хугацаа хэтэрсэн зээлийн өртэй хуулийн этгээд; 39.7.9.Yйлдвэрчний эвлэл, шашны болон бусад төрийн бус байгууллага; 39.7.10.байгуулагдаад нэг жил болоогүй хуулийн этгээд.\r\n', '39.11.Энэ хуулийн 39.1-д заасан хэмжээнээс илүү гарсан, түүнчлэн энэ хуулийн 39.7-д заасан этгээдээс авсан хандивыг хурааж, улсын орлого болгоно.              39.12.Энэ хуулийн 39.7-д заасан этгээдээс хандив авч зарцуулсан нь нэр дэвшигчийг нэрийн жагсаалтаас хасах үндэслэл болно.\r\n', 'МОНГОЛ УЛСЫН ИХ ХУРЛЫН СОНГУУЛИЙН ТУХАЙ', NULL),
(50, '39.8.Сонгууль зохион байгуулах хугацаанд гадаад, дотоодын тусламжийн эд хөрөнгийг иргэдэд тараах, худалдан борлуулж сонгуульд зарцуулахыг хориглоно.\r\n', NULL, 'МОНГОЛ УЛСЫН ИХ ХУРЛЫН СОНГУУЛИЙН ТУХАЙ', NULL),
(51, '39.9.Сонгуулийн жил зөвхөн сонгуулийн үйл ажиллагаанд зориулж хандив өгөх бөгөөд энэ тохиолдолд Улс төрийн намын тухай хуулийн 18.3-т заасан хандивыг нам, эвсэлд давхардуулан өгөхийг хориглоно.\r\n', NULL, 'МОНГОЛ УЛСЫН ИХ ХУРЛЫН СОНГУУЛИЙН ТУХАЙ', NULL),
(52, '39.10.Энэ хуулийн 39.1, 39.3, 39.4, 39.9-д заасныг зөрчсөн иргэнийг нэг сарын хөдөлмөрийн хөлсний доод хэмжээг гурваас тав дахин нэмэгдүүлсэнтэй тэнцэх хэмжээний төгрөгөөр, хуулийн этгээдийг нэг сарын хөдөлмөрийн хөлсний доод хэмжээг таваас долоо дахин нэмэгдүүлсэнтэй тэнцэх хэмжээний төгрөгөөр, энэ хуулийн 39.6-39.8-д заасныг зөрчсөн нам, эвслийг нэг сарын хөдөлмөрийн хөлсний доод хэмжээг таваас долоо дахин нэмэгдүүлсэнтэй тэнцэх хэмжээний төгрөгөөр, энэ хуулийн 39.6-д заасныг зөрчсөн бие даан нэр дэвшигчийг нэг сарын хөдөлмөрийн хөлсний доод хэмжээг гурваас тав дахин нэмэгдүүлсэнтэй тэнцэх хэмжээний төгрөгөөр, энэ хуулийн 39.7.3, 39.7.7-39.7.10-т заасныг зөрчсөн хуулийн этгээдийг нэг сарын хөдөлмөрийн хөлсний доод хэмжээг таваас долоо дахин нэмэгдүүлсэнтэй тэнцэх хэмжээний төгрөгөөр, албан тушаалтныг нэг сарын хөдөлмөрийн хөлсний доод хэмжээг гурваас тав дахин нэмэгдүүлсэнтэй тэнцэх хэмжээний төгрөгөөр, энэ хуулийн 39.8-д заасныг зөрчсөн нэр дэвшигч, нам, эвслийн сонгууль эрхэлсэн байгууллагын ажилтан, нэр дэвшигчийн шадар туслагч, ухуулагчийг нэг сарын хөдөлмөрийн хөлсний доод хэмжээг гурваас тав дахин нэмэгдүүлсэнтэй тэнцэх хэмжээний төгрөгөөр тус тус торгоно.\r\n', NULL, 'МОНГОЛ УЛСЫН ИХ ХУРЛЫН СОНГУУЛИЙН ТУХАЙ', NULL),
(53, '39.11.Энэ хуулийн 39.1-д заасан хэмжээнээс илүү гарсан, түүнчлэн энэ хуулийн 39.7-д заасан этгээдээс авсан хандивыг хурааж, улсын орлого болгоно.\r\n', NULL, 'МОНГОЛ УЛСЫН ИХ ХУРЛЫН СОНГУУЛИЙН ТУХАЙ', NULL),
(54, '39.12.Энэ хуулийн 39.7-д заасан этгээдээс хандив авч зарцуулсан нь нэр дэвшигчийг нэрийн жагсаалтаас хасах үндэслэл болно.\r\n', NULL, 'МОНГОЛ УЛСЫН ИХ ХУРЛЫН СОНГУУЛИЙН ТУХАЙ', NULL),
(55, '40.1.Сонгуулийн зардлын дансанд мөнгөн хөрөнгийг төвлөрүүлэх, түүнийг зарцуулсан тухай тайлангийн загвар болон данс нээх, хаах, бүртгэл, тайланг хөтлөх журмыг Сонгуулийн ерөнхий хороо Монголбанктай хамтран батална.\r\n', NULL, 'МОНГОЛ УЛСЫН ИХ ХУРЛЫН СОНГУУЛИЙН ТУХАЙ', NULL),
(56, '40.2.Нам, эвслээс нэр дэвшигч нь санал хураалт дууссан өдрөөс хойш 15 хоногт багтаан өөрийн сонгуулийн зардлын тайлангаа нам, эвсэлдээ гаргаж өгөх бөгөөд нам, эвсэл болон бие даан нэр дэвшигч нь сонгуулийн зардлын тайланг санал хурааж дууссан өдрөөс хойш нэг сарын дотор гаргаж, Сонгуулийн ерөнхий хороонд ирүүлнэ.\r\n', '40.8.Энэ хуулийн 40.2-40.4-т заасныг зөрчсөн нам, эвслийг нэг сарын хөдөлмөрийн хөлсний доод хэмжээг таваас долоо дахин нэмэгдүүлсэнтэй тэнцэх хэмжээний төгрөгөөр, нэр дэвшигчийг нэг сарын хөдөлмөрийн хөлсний доод хэмжээг хоёроос дөрөв дахин нэмэгдүүлсэнтэй тэнцэх хэмжээний төгрөгөөр, ... тус тус торгоно.\r\n', 'МОНГОЛ УЛСЫН ИХ ХУРЛЫН СОНГУУЛИЙН ТУХАЙ', NULL),
(57, '40.3.Нам, эвсэл, бие даан нэр дэвшигч энэ хуулийн 40.2-т заасан сонгуулийн зардлын тайлан /цаашид “зардлын тайлан” гэх/-д аудит хийлгэж, дүгнэлт гаргуулсан байна.\r\n', '40.8.Энэ хуулийн 40.2-40.4-т заасныг зөрчсөн нам, эвслийг нэг сарын хөдөлмөрийн хөлсний доод хэмжээг таваас долоо дахин нэмэгдүүлсэнтэй тэнцэх хэмжээний төгрөгөөр, нэр дэвшигчийг нэг сарын хөдөлмөрийн хөлсний доод хэмжээг хоёроос дөрөв дахин нэмэгдүүлсэнтэй тэнцэх хэмжээний төгрөгөөр, ... тус тус торгоно.\r\n', 'МОНГОЛ УЛСЫН ИХ ХУРЛЫН СОНГУУЛИЙН ТУХАЙ', NULL),
(58, '40.4.Зардлын тайланг дараах үзүүлэлтээр гаргана: 40.4.1.энэ хуулийн 38.1, 38.2-т заасан дансанд төвлөрүүлсэн орлого, нийт хандивын хэмжээ;40.4.2.энэ хуулийн 40.4.1-д заасан орлогын эх үүсвэр, түүний дотор хандивын төрөл, хэмжээ; 40.4.3.хандивлагч иргэний эцэг /эх/-ийн нэр, өөрийн нэр, оршин суугаа газрын хаяг, хандивын хэмжээ, хэлбэр; 40.4.4.хандивлагч хуулийн этгээдийн нэр, оршин байгаа газрын хаяг, гүйцэтгэх захирлын эцэг /эх/-ийн нэр, өөрийн нэр, хандивын хэмжээ, хэлбэр; 40.4.5.зарлагын ангилал, гүйцэтгэл; 40.4.6.үлдэгдлийн хэмжээ, байршил.\r\n', '40.8.Энэ хуулийн 40.2-40.4-т заасныг зөрчсөн нам, эвслийг нэг сарын хөдөлмөрийн хөлсний доод хэмжээг таваас долоо дахин нэмэгдүүлсэнтэй тэнцэх хэмжээний төгрөгөөр, нэр дэвшигчийг нэг сарын хөдөлмөрийн хөлсний доод хэмжээг хоёроос дөрөв дахин нэмэгдүүлсэнтэй тэнцэх хэмжээний төгрөгөөр, ... тус тус торгоно.\r\n', 'МОНГОЛ УЛСЫН ИХ ХУРЛЫН СОНГУУЛИЙН ТУХАЙ', NULL),
(59, '40.5.Нам, эвсэл, бие даан нэр дэвшигчийн сонгуулийн зардлын данс байршуулсан банкны захирал нь уг дансны бүх гүйлгээг нэгтгэсэн тайлан гаргаж, энэ хуулийн 40.2-т заасан хугацаанд Сонгуулийн ерөнхий хороонд ирүүлнэ.\r\n', '40.8.Энэ хуулийн ... 40.5-д заасныг зөрчсөн банкны захирлыг нэг сарын хөдөлмөрийн хөлсний доод хэмжээг гурваас тав дахин нэмэгдүүлсэнтэй тэнцэх хэмжээний төгрөгөөр тус тус торгоно.\r\n', 'МОНГОЛ УЛСЫН ИХ ХУРЛЫН СОНГУУЛИЙН ТУХАЙ', NULL),
(60, '40.6.Сонгуулийн ерөнхий хороо зардлын тайланг хянаж, санал хураалт дууссан өдрөөс хойш 45 хоногийн дотор дүнг нийтэд мэдээлэх ба хоёр зуун мянга, түүнээс дээш төгрөгийн хандив өгсөн иргэн, таван зуун мянга, түүнээс дээш төгрөгийн хандив өгсөн хуулийн этгээдийг нийтэд зарлана.\r\n', NULL, 'МОНГОЛ УЛСЫН ИХ ХУРЛЫН СОНГУУЛИЙН ТУХАЙ', NULL),
(61, '40.7.Сонгуулийн ерөнхий хороо шаардлагатай гэж үзвэл нам, эвсэл, нэр дэвшигчийн сонгуулийн зардлын санхүүжилт, түүний гүйцэтгэлийг төрийн аудитын байгууллагаар шалгуулж, дүгнэлт гаргуулна.\r\n', NULL, 'МОНГОЛ УЛСЫН ИХ ХУРЛЫН СОНГУУЛИЙН ТУХАЙ', NULL),
(62, '40.8.Энэ хуулийн 40.2-40.4-т заасныг зөрчсөн нам, эвслийг нэг сарын хөдөлмөрийн хөлсний доод хэмжээг таваас долоо дахин нэмэгдүүлсэнтэй тэнцэх хэмжээний төгрөгөөр, нэр дэвшигчийг нэг сарын хөдөлмөрийн хөлсний доод хэмжээг хоёроос дөрөв дахин нэмэгдүүлсэнтэй тэнцэх хэмжээний төгрөгөөр, энэ хуулийн 40.5-д заасныг зөрчсөн банкны захирлыг нэг сарын хөдөлмөрийн хөлсний доод хэмжээг гурваас тав дахин нэмэгдүүлсэнтэй тэнцэх хэмжээний төгрөгөөр тус тус торгоно.\r\n', NULL, 'МОНГОЛ УЛСЫН ИХ ХУРЛЫН СОНГУУЛИЙН ТУХАЙ', NULL),
(63, '"7 дугаар зүйл. Санхүүгийн тайлангаа баталгаажуулах байгууллага\r\n1. Дараахь аж ахуйн нэгж, байгууллага санхүүгийн тайлангаа аудитын байгууллагаар заавал баталгаажуулна: 10/ улс төрийн нам"\r\n', NULL, 'АУДИТЫН ТУХАЙ', NULL);
